import React from 'react'
import ReactDOM from 'react-dom/client';
import styles from '../components/home/home.module.scss'
import { useState } from 'react';
import { useEffect } from 'react';




const Home:React.FC = () => {
    const [emailTo, setEmailTo] = useState('')
    const [emailSubject, setEmailSubject] = useState('')
    const [emailText, setEmailText] = useState('')
    const [message, setMessage] = useState('');




    const UserSendEmail = async (e:React.FormEvent) => {
            e.preventDefault()
            try {
                const response = await fetch(`http://localhost:9004/submit`, {
                    method:'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({emailTo, emailSubject, emailText})
                });
                    if(response.ok) {
                        setMessage('User Response Email has been sent')
                    } else {
                        setMessage('Email has not been sent')
                    }
                 }catch(error) {
                    setMessage('Email has not been sent')
            }
    }

  return (
   <>

        <div className={styles.container}>
            <div className={styles.header}><h3>Send A Friend an Email</h3></div>
            <form className={styles.form}  onSubmit={UserSendEmail}>
                <div><input onChange={(e) => setEmailSubject(e.target.value)} type='text' value={emailSubject} placeholder='Subject Line' required></input></div>
                <div><input onChange={(e) => setEmailTo(e.target.value)} type='email' value={emailTo} placeholder='Friend Email' required></input></div>
                <div><textarea onChange={(e) => setEmailText(e.target.value)} cols={50} rows={3} placeholder='Message' required></textarea></div>
                <button type='submit'>Send Email</button>
            </form>
        </div>

            <br />

            <div>
                {message && <p>{message}</p>}
            </div>



   </>
  )
}

export default Home
