import React from 'react'
import ReactDOM from 'react-dom/client';
import styles from '../components/home/home.module.scss'
import { useState } from 'react';
import { useEffect } from 'react';




const Home:React.FC = () => {
    const [emailTo, setEmailTo] = useState('')
    const [emailSubject, setEmailSubject] = useState('')
    const [emailText, setEmailText] = useState('')
    const [message, setMessage] = useState('');


    useEffect(() => {
        document.body.classList.add('landpage-background')
        return () => {
            document.body.classList.remove('landpage-background')
        }
    }, [])


    const UserSendEmail = async (e:React.FormEvent) => {
            e.preventDefault()
            try {
                const response = await fetch(`http://localhost:9004/submit`, {
                    method:'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({emailTo, emailSubject, emailText})
                });
                    if(response.ok) {
                        setMessage('User Response Email has been sent')
                    } else {
                        setMessage('Email has not been sent')
                    }
                 }catch(error) {
                    setMessage('Email has not been sent')
            }
    }

  return (
   <>

        <div className={styles.landpage-background}></div>



   </>
  )
}

export default Home
